mapper.insert:
INSERT INTO CPC_TREE
(
PARENT_SYMBOL
, PARENT_LVL
, SYMBOL
, LVL
, SORT_KEY
, BREAKDOWN_CODE
, NOT_ALLOCATABLE
, ADDITIONAL_ONLY
, DEFINITION_EXISTS
, DATE_REVISED
, STATUS
)

    SELECT
    #{cpcTree.parentSymbol}


            , #{cpcTree.parentLevel}


            , NULL


    , #{cpcTree.symbol}
    , #{cpcTree.level}
    , #{cpcTree.sortKey}
    , #{cpcTree.breakdownCode}
    , #{cpcTree.notAllocatable}
    , #{cpcTree.additionalOnly}
    , #{cpcTree.definitionExists}
    , #{cpcTree.dateRevised}
    , #{cpcTree.status}
    FROM DUAL

INSERT INTO CPC_TITLE
(
SYMBOL
, LVL
, TITLE
, DATE_REVISED
)

    SELECT
    #{cpcTitle.symbol}
    , #{cpcTitle.level}
    , #{cpcTitle.title}
    , #{cpcTitle.dateRevised}
    FROM DUAL

INSERT INTO CPC_MEDIA
(
SYMBOL
, LVL
, ID
, FILE_NAME
, STORE_PATH
, "TYPE"
)
SELECT DISTINCT *
FROM (

    SELECT #{cpcMedia.symbol}
    , #{cpcMedia.level}
    , #{cpcMedia.id}
    , #{cpcMedia.fileName}
    , #{cpcMedia.storePath}
    , #{cpcMedia.type}
    FROM DUAL

)

INSERT INTO CPC_NOTEANDWARNING
( SYMBOL
, LVL
, "TYPE"
, SEQ
, "DEPTH"
, SUBNOTE_TYPE
, CONTENT
, DATE_REVISED
)

    SELECT #{cpcNw.symbol}
    , #{cpcNw.level}
    , #{cpcNw.type}
    , #{cpcNw.seq}
    , #{cpcNw.depth}
    , #{cpcNw.subnoteType}
    , #{cpcNw.content}
    , #{cpcNw.dateRevised}
    FROM DUAL
